import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [restaurant, setRestaurant] = useState('');
  const [party, setParty] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [note, setNote] = useState('');
  const [responseMessage, setResponseMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:6969/api/reservation', {
        restaurant: restaurant,
        party: parseInt(party),
        date: date,
        time: time,
        note: note
      });
      setResponseMessage(`Reservation added: ${response.data.restaurant}`);
    } catch (err) {
      setResponseMessage('Error adding reservation');
    }
  };

  return (
    <div className="App">
      <h1>Add Reservation</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Restaurant Name"
          value={restaurant}
          onChange={(e) => setRestaurant(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Party Number"
          value={party}
          onChange={(e) => setParty(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <input
          type="time"
          placeholder="Time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Note"
          value={note}
          onChange={(e) => setNote(e.target.value)}
        />
        <button type="submit">Add Reservation</button>
      </form>
      {responseMessage && <p>{responseMessage}</p>}
    </div>
  );
}

export default App;
