import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [restaurant, setRestaurant] = useState('');
  const [party, setParty] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [note, setNote] = useState('');
  const [responseMessage, setResponseMessage] = useState('');
  const [reservations, setReservations] = useState([]);

  // call to server to get reservations
  const fetchReservations = async () => {
    try {
      const response = await axios.get('http://localhost:6969/api/getreservations');
      setReservations(response.data);
    } catch (err) {
      console.error("Error fetching reservations", err);
    }
  };

  useEffect(() => {
    fetchReservations();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:6969/api/reservation', {
        restaurant: restaurant,
        party: parseInt(party),
        date: date,
        time: time,
        note: note
      });
      setResponseMessage(`Reservation added: ${response.data.restaurant}`);


      fetchReservations();
      setRestaurant('');
      setParty('');
      setDate('');
      setTime('');
      setNote('');
    } catch (err) {
      setResponseMessage('Error adding reservation');
    }
  };

  return (
    <div className="App">
      { /* Add Reservations */}
      <h3>Add Reservation</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Restaurant Name"
          value={restaurant}
          onChange={(e) => setRestaurant(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Party Number"
          value={party}
          onChange={(e) => setParty(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <input
          type="time"
          placeholder="Time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Note"
          value={note}
          onChange={(e) => setNote(e.target.value)}
        />
        <button type="submit">Add Reservation</button>
      </form>
      {responseMessage && <p>{responseMessage}</p>}

      { /* Display Reservations */}
      <h3>My Reservations</h3>
      {/*<ul>
        {reservations.map((reservation) => (
          <li key={reservation._id}> className="reservation-item"
            <strong>{reservation.restaurant}</strong>
            <p>Party: {reservation.party}</p>
            <p>
              Date: {new Date(reservation.date).toLocaleDateString()}<br />
              Time: {reservation.time}
            </p>
            <p>Note: {reservation.note}</p>
          </li>
        ))}
      </ul>*/}

      <div className="reservation-list">
        {reservations.map((reservation) => (
          <div key={reservation._id} className="reservation-item">
            <input type="text" value={reservation.name} disabled />
            <input type="number" value={`Party of ${reservation.party}`} disabled />
            <input type="date" value={new Date(reservation.date).toISOString().split('T')[0]} disabled />
            <input type="time" value={reservation.time} disabled />
            <input type="text" value={reservation.note || ''} disabled />
          </div>
        ))}
      </div>


    </div>

  );
}

export default App;
