import React, { useState, useEffect } from 'react';
import './App.css'

function App() {
  const [restaurant, setRestaurant] = useState('');
  const [party, setParty] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [note, setNote] = useState('');
  const [responseMessage, setResponseMessage] = useState('');
  const [reservations, setReservations] = useState([]);

  // for editing:
  const [editParty, setEditParty] = useState({});
  const [editDate, setEditDate] = useState({});
  const [editTime, setEditTime] = useState({});
  const [isEditing, setIsEditing] = useState(false);

  // call to server to get reservations
  const fetchReservations = async () => {
    try {
      const response = await fetch('http://localhost:6969/reservation');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setReservations(data);
    } catch (err) {
      console.error("Error fetching reservations", err);
    }
  };

  useEffect(() => {
    fetchReservations();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:6969/reservation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify ({
          restaurant: restaurant,
          party: parseInt(party),
          date: date,
          time: time,
          note: note
        }),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setResponseMessage(`Reservation added: ${data.restaurant}`);

      fetchReservations();
      setRestaurant('');
      setParty('');
      setDate('');
      setTime('');
      setNote('');
    } catch (err) {
      setResponseMessage('Error adding reservation');
    }
  };

  // call to server to edit reservations
  const handleEdit = async (reservationId, updatedReservation) => {
    try {
      const response = await fetch(`http://localhost:6969/reservation/${reservationId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedReservation),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setResponseMessage(`Reservation edited: ${data.restaurant}`);
      fetchReservations();
    } catch (err) {
      setResponseMessage('Error editing reservation');
    }
  };

  // call to server to delete reservations
  const handleDelete = async (reservationId) => {
    try {
      const response = await fetch(`http://localhost:6969/reservation/${reservationId}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      } 
    } catch (err) {
      setResponseMessage('Error deleting reservation');
    }
    fetchReservations();
  };

  return (
    <div className="App">
      { /* Add Reservations */}
      <h3>Add Reservation</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Restaurant Name"
          value={restaurant}
          onChange={(e) => setRestaurant(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Party Number"
          value={party}
          onChange={(e) => setParty(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <input
          type="time"
          placeholder="Time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Note"
          value={note}
          onChange={(e) => setNote(e.target.value)}
        />
        <button type="submit">Add Reservation</button>
      </form>
      {responseMessage && <p>{responseMessage}</p>}

      { /* Display Reservations */}
      <div className="reservation-container">
        <h3>My Reservations</h3>

        {/* Header Row */}
        <div className="reservation-header">
          <div>Restaurant Name</div>
          <div>Party</div>
          <div>Date</div>
          <div>Time</div>
          <div>Note</div>
          <div>Actions</div>
        </div>

        {/* Reservation Entries */}
        <div className="reservation-list">
          {reservations.map((reservation) => (
            <div key={reservation._id} className="reservation-item">
              <input type="text" value={reservation.restaurant} disabled />
              <input type="number" value={reservation.party} 
                onChange = {(e) => setEditParty({...editParty, [reservation._id]: e.target.value})}
              disabled />
              <input type="date" value={new Date(reservation.date).toISOString().split('T')[0]} 
                onChange = {(e) => setEditDate({...editDate, [reservation._id]: e.target.value})}
              disabled />
              <input type="time" value={reservation.time} 
                onChange = {(e) => setEditTime({...editTime, [reservation._id]: e.target.value})}
              disabled />
              <input type="text" value={reservation.note || ''} disabled />

              <button onClick={() => handleEdit(reservation._id,
                {party: parseInt(party),
                date: date,
                time: time,}
              )}> Edit</button>


              <button onClick={() => handleDelete(reservation._id)}>Delete</button>
            </div>
          ))}
        </div>
      </div>



    </div>

  );
}

export default App;
